import {LOADING_USER,LOADING_UI, SET_GOOGLE_USER} from "../store/types"
import * as firebase from "firebase"
import Config from "../../config/config"
import axios from "axios"

firebase.initializeApp(Config)


export const authenticateGoogle = () => (dispatch) => {
    dispatch({type: LOADING_UI})
    const provider = new firebase.auth.GoogleAuthProvider()
    provider.addScope('profile');
    provider.addScope('email');
    firebase.auth().signInWithPopup(provider)
    .then((result) =>{
        if (result.credential) {
        const {user,credential} = result   
        const {accessToken} = credential 
        console.log(user)   
        const  {displayName, email,uid} = user
        const userData = {
             email: email,
             username: displayName
        }
        localStorage.setItem("token", accessToken)
        dispatch(googleSignup(userData,uid,user))
        }


      }).then 
    .catch((err) => console.log(err));


}


export const googleSignup = (userData,userId,user) => (dispatch) => {
    dispatch({type: LOADING_USER})
    axios.post(`/google/${userId}`,userData)
    .then(res=> {
        dispatch({
            type: SET_GOOGLE_USER,
            payload: res.data
        })

        console.log(user)
        // window.location.href = "/"
    })
    .catch((err) => console.log(err));

}
