import React from "react"
import { Comment } from "semantic-ui-react"
import moment from "moment"

const isOwnMessage = (message, user) => {
    return message.user.id === user.userId ? "message__self" : ''
}
const isOwnMessageTwo = (message, user) => {
    return message.user.id === user.userId ? "message__selfTwo" : ''
}

const timeFromNow = (timestamp) => moment(timestamp).fromNow()

const Message = ({ message, user }) => (
    <Comment >
        <Comment.Avatar className={isOwnMessageTwo(message, user)}  src={message.user.avatar} />
        <Comment.Content className={isOwnMessage(message, user)} >
        <Comment.Author as="a"  className={isOwnMessageTwo(message, user)} >{message.user.name}</Comment.Author>
        <Comment.Metadata  className={isOwnMessageTwo(message, user)} >{timeFromNow(message.timestamp)}</Comment.Metadata>
        <Comment.Text  className={isOwnMessageTwo(message, user)} >{message.content}</Comment.Text>
        <br/>
        {/* <hr/> */}
        </Comment.Content>
    </Comment>
)
export default Message
