import React, { Component } from 'react'
import { Grid, Header, Icon, Dropdown } from "semantic-ui-react"
import firebase from "../../Auth/firebase"


class userpanel extends Component {
    dropDownOptions = () => [
        { text: <span >Signed as <strong>User</strong></span>,disabled: true},
        {key: "avatar",text: <span>Change Avatar</span> },
        { key: "signout",text: <span onClick={this.handleSignout }>Sign out</span>}
    ]
    handleSignout =()=> {
        firebase.auth().signOut().then(()=> {
            console.log("Signed out")
        })
    }

    render() {
        return (
            <Grid style={{ background: "#004d40" }}>
                <Grid.Column>
                    <Grid.Row style={{ padding: '1.2em', margin: 0 }}>
                        <Header inverted floated="left" as="h2"  className="animated slideInLeft delay-1s">
                            <Icon name="meetup" />
                            <Header.Content style={{ fontSize: 20 }} >Meet-Friends</Header.Content>
                        </Header>
                    </Grid.Row>
                    <Header style={{ padding: '0.25em' }} as="h4" inverted>
                        <Dropdown trigger={
                            <span>
                                User
                            </span>
                        }
                            options={this.dropDownOptions()}
                        />
                    </Header>
                </Grid.Column>
            </Grid>
        )
    }
}

export default userpanel
