import { NEW_CHANNEL,LOADING_DATA} from "../store/types"

const initialData = {
    channels: [],
    newChannel: {},
    loading: false
}

export default add_channel_reducer =(state= initialData,actions) =>{
    switch(actions.types){
        case LOADING_DATA: return{loading: true}
        case NEW_CHANNEL: return{loading: false, newChannel: actions.payload}
        default: return state
    }
}
