import React, { Component } from 'react'
import landingPage from './components/landingPage/landingPage'
import './App.css';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import login from './pages/login/login'
import { Provider } from "react-redux"
import store from "./redux/store/store"
import AuthRoute from "./Auth/AuthRoute"
import { SET_AUTHENTICATED } from "./redux/store/types"
import axios from "axios"
import jwtDecode from "jwt-decode"

let tokenData = localStorage.token
tokenData = tokenData.replace(/[\u0000-\u0019]+/g, "")
tokenData = tokenData.replace(/\\n/g, "\\n").replace(/\\'/g, "\\'").replace(/\\"/g, '\\"').replace(/\\&/g, "\\&").replace(/\\r/g, "\\r").replace(/\\t/g, "\\t").replace(/\\b/g, "\\b").replace(/\\f/g, "\\f")
tokenData = tokenData.replace(/[\u0000-\u0019]+/g, "");

const token = JSON.parse(tokenData)
console.log(token)
if (tokenData) {

  const decodeToken = jwtDecode(token)
  console.log(decodeToken)
}
