import React, { Component } from 'react'
import { authenticateGoogle } from "../../redux/actions/userActions"
import PropTypes from "prop-types"
import { connect } from "react-redux"




class googleAuth extends Component {
    constructor(){
        super()
        this.state={
            errors: {}
        }
    }
    componentWillReceiveProps(nxtProps){
        if(nxtProps.UI.errors){
            this.setState({errors: nxtProps.UI.errors})
        }
    }

    handleLogin =(event)=> {
        event.preventDefault()
        this.props.authenticateGoogle(this.props.history )
    }


    render() {
        return (
            <button onClick={() => this.handleLogin()}>
                Google
            </button>
        )
    }
}

// export default googleAuth

googleAuth.propTypes = {
    authenticateGoogle: PropTypes.func.isRequired
}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

export default connect(mapStateToProps, { authenticateGoogle })(googleAuth)
