import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField'
import Google from "../../components/signInMethods/googleAuth"
import Github from "../../components/signInMethods/githubAuth"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import { loginUser } from "../../redux/actions/userActions";
import CircularProgress from "@material-ui/core/CircularProgress";
import Typography from "@material-ui/core/Typography";
import PropTypes from "prop-types"
import { connect } from "react-redux"
import { MuiThemeProvider } from "@material-ui/core/styles";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import themeObject from "../../theme/theme"
import register from './register'


const theme = createMuiTheme(themeObject);

class login extends Component {

    constructor() {
        super();
        this.state = {
            email: "",
            password: "",
            errors: {},
        };
    }
    componentWillReceiveProps(nextProps) {
        if (nextProps.UI.errors) {
            this.setState({ errors: nextProps.UI.errors });
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.setState({
            loading: true,
        });
        const userData = {
            email: this.state.email,
            password: this.state.password,
        };

        this.props.loginUser(userData, this.props.history);
    };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value,
        });
    };

    render() {
        const {
            UI: { loading },
        } = this.props;
        const { errors } = this.state;
        return (
            <MuiThemeProvider theme={theme}>
                <div className="login">
                    <Grid container direction="row">
                        <Grid item xs={12} md={3} />
                        <Grid item xs={12} md={6}>
                            <Grid container direction="column">
                                <Grid item xs={12} md={3} />
                                <Grid item xs={12} md={3} >
                                    <form noValidate onSubmit={this.handleSubmit} className="" >

                                        {errors.message && (<Typography variant="body2">{errors.message}</Typography>)}

                                        <TextField id="email" name="email" type="email" label="Email"
                                            value={this.state.email}
                                            onChange={this.handleChange}
                                            helperText={errors.email}
                                            error={errors.email ? true : false}
                                            fullWidth
                                        /> <br />
                                        <TextField id="password" name="password" type="password" label="Password"
                                            value={this.state.password}
                                            onChange={this.handleChange}
                                            helperText={errors.password}
                                            error={errors.password ? true : false}

                                        /> <br /> <br />
                                        {errors.general && (<Typography variant="body2">{errors.general}</Typography>)}
                                        <Button type="submit" variant="contained" id="button" color="primary" disabled={loading}>Login
{loading && (<CircularProgress size={30} disableShrink />)}
                                        </Button><br />
                                        <small className="">Don't have an account ? Signup <Link to="/signup" component={register}>here</Link>  </small>
                                    </form>
                                </Grid>
                                <hr />
                                <Grid item xs={12} md={6}>
                                    {/* Sign in with: <br/> */}
                                    <Typography variant="button" className="button">
                                        <Google />
                                        <Github />


                                    </Typography>


                                </Grid>
                            </Grid>
                        </Grid>

                        <Grid item xs={12} md={3} />

                    </Grid>

                </div >
            </MuiThemeProvider>
        )
    }
}

login.propTypes = {
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired,
    loginUser: PropTypes.func.isRequired,


}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

export default connect(mapStateToProps, { loginUser })(login)
