    render() {
        const { modal, closeModal } = this.props


        return (
            <Modal
                basic 
                open={modal}
                onClose={closeModal}
            >
                <Modal.Header>
                    Select an Image File
                </Modal.Header>
                <Modal.Content>
                    <Input
                    onChange={this.addFile}
                        fluid label="File types: jpg, png"
                        name="file"
                        type="file"
                    />
                </Modal.Content>
                <Modal.Actions>
                    <Button
                        onClick={this.sendFile}
                        color="green"
                        inverted
                    >
                        <Icon name="checkmark" /> Send
                    </Button>
                    <Button
                        color="red"
                        inverted
                        onClick={closeModal}
                    >
                        <Icon name="remove" /> Cancel
                    </Button>
                </Modal.Actions>

            </Modal>
        )
    }
}

export default fileModal
