import {LOADING_USER,LOADING_UI, SET_GOOGLE_USER,SET_ERRORS,CLEAR_ERRORS} from "../store/types"
import * as firebase from "firebase"
import Config from "../../config/config"
import axios from "axios"
import jwt_decode from "jwt-decode"

firebase.initializeApp(Config)

const setAuthorizationHeader = (token) => {
    const authToken = `Bearer ${token}`;
    localStorage.setItem("token", token);
    axios.defaults.headers.common["Authorization"] = authToken;
  };

export const multiAuth = (provider,firstScope,secondScope) => (dispatch)=> {
    provider.addScope(`${firstScope}`);
    if(secondScope)provider.addScope(`${secondScope}`)
    firebase.auth().signInWithPopup(provider)
    .then((result) =>{
        if (result.credential) {
        const {user,credential} = result
        const {accessToken} = credential
        let token;   
        console.log(accessToken)   
        const  {displayName, email,xa} = user
        console.log(firebase.auth().currentUser)
        token = xa
        localStorage.setItem("token", token)
        const {picture,user_id} =  jwt_decode(token)   

        const userData = {
             email: email,
             username: displayName,
             profileImage: picture
        }

        dispatch(googleSignup(userData,user_id))
        }   
      }) 
    .catch((err) => {
        dispatch({
            type: SET_ERRORS,
            payload: err
        })
        console.log(err)
    });

}


export const authenticateGoogle = () => (dispatch) => {
    dispatch({type: LOADING_UI})
    const provider = new firebase.auth.GoogleAuthProvider()
    let profile = "profile"
    let email = "email"
    dispatch(multiAuth(provider,profile,email))

}

export const authenticateGithub = () => (dispatch) => {
    dispatch({type: LOADING_UI})
    const provider = new firebase.auth.GithubAuthProvider()
    let repo = "repo"
    // let email = "email"
    dispatch(multiAuth(provider,repo))
}


export const googleSignup = (userData,userId) => (dispatch) => {
    dispatch({type: LOADING_USER})
    axios.post(`/google/${userId}`,userData)
    .then(res=> {
        dispatch({
            type: SET_GOOGLE_USER,
            payload: res.data
        })

        // window.location.href = "/"
    })
    .catch((err) => console.log(err));

}

export const loginUser = (userData, history) => (dispatch) => {
    dispatch({ type: LOADING_UI });
    axios
      .post("/login", userData)
      .then( (res) => {
        setAuthorizationHeader(res.data.token);
        dispatch({ type: CLEAR_ERRORS }); 
          const {token} = res.data;
          localStorage.setItem("token", token);
          history.push("/")
          window.location.reload();

      })
      .catch((err) => {
        dispatch({
          type: SET_ERRORS,
          payload: err.response.data,
        });
      });
  };

