import React, { Component, Fragment } from 'react'
import { Menu, Icon,Modal,Form,Input,Button } from "semantic-ui-react"
import firebase from "../../Auth/firebase"
import { Loader, Dimmer } from "semantic-ui-react";



class Channels extends Component {
    state = {
        channels: [],
        channelName: "",
        channelDetails: "",
        modal: false,
        channelsRef: firebase.database().ref('channels'),
        errors:{}

    }
    closeModal = () => {this.setState({modal: false})}

    openModal = () => {this.setState({modal: true}) }

    handleChange =(event) => {
        event.preventDefault()
        this.setState({[event.target.name]: event.target.value})
    }
    componentDidMount() {
        this.addListeners()
    }

    addListeners = ()=> {
        let loadedChannels = []
        this.state.channelsRef.on("child_added", snap => {
            loadedChannels.push(snap.val())
            this.setState({channels: loadedChannels})
        })
    }

    addChannel = () => {
        const {channelsRef,channelName,channelDetails,user} = this.state
        const {username,email,userId,profileImage} = this.props.authenticatedUser
        const key = channelsRef.push().key
        const newChannel = {
           id: key,
           name:channelName,
           details: channelDetails,
           createdBy: {
            name:username,
            email: email,
            avatar:profileImage,
            userId: userId
           }
       }
       channelsRef.child(key).update(newChannel).then(()=> {
           this.setState({ channelName: "",channelDetails: ""})
           this.closeModal()
           console.log("Channel added")
       }).catch((error)=> {
           console.error(error)
       })
    }
    handleSubmit =(event) => {
        event.preventDefault()
        if(this.isformValid(this.state)){
        this.addChannel()
        }
    }
    isformValid = ({channelName,channelDetails}) => channelName && channelDetails;



    render() {
        const { modal,channels,errors } = this.state
        return (
            <Fragment>
                <Menu.Menu style={{ paddingBottom: "2em" }}>
                <Menu.Item>
                    <span><Icon name="exchange" /> CHANNELS </span> {" "}
               ({channels.length}) <Icon name="add" className="add" onClick={this.openModal} />

                </Menu.Item>
                {this.displayChannels}
            </Menu.Menu>

            <Modal basic open={modal} onClose={this.closeModal}>
                {this.props.authenticatedUser? (
                  <Fragment>
                        <Modal.Header>Add a Channel </Modal.Header>
                    <Modal.Content>
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Field>
                                <Input fluid label="Name of channel" name="channelName" onChange={this.handleChange}
                                error={errors.channelName ? true: false}

                                />
                            </Form.Field>
                            <Form.Field>
                                 <Input fluid label="About the channel" name="channelDetails" onChange={this.handleChange}/>
                            </Form.Field>
                        </Form>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button inverted color="green" onClick={this.handleSubmit}>
                            <Icon name="checkmark" /> Add
                        </Button>
                        <Button inverted color="red" onClick={this.closeModal}>
                            <Icon name="remove" /> Cancel
                        </Button>
                    </Modal.Actions>
                  </Fragment>
                ):(
                    <Dimmer active>
                     <Loader size="huge" content="Please Wait authenticating..." />
                   </Dimmer>
               )}

            </Modal>
            </Fragment>

        )
    }
}


export default Channels
