import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField'
import Google from "../../components/signInMethods/googleAuth"
import Github from "../../components/signInMethods/githubAuth"
import Button from "@material-ui/core/Button"
import { Grid, Header, Dropdown, Icon, Card, Form } from "semantic-ui-react"
import { loginUser } from "../../redux/actions/userActions";
import CircularProgress from "@material-ui/core/CircularProgress";
import Typography from "@material-ui/core/Typography";
import PropTypes from "prop-types"
import { connect } from "react-redux"
import { MuiThemeProvider } from "@material-ui/core/styles";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import themeObject from "../../theme/theme"
import { CLEAR_ERRORS } from "../../redux/store/types"
import store from "../../redux/store/store"



const theme = createMuiTheme(themeObject);



class login extends Component {

    constructor() {
        super();
        this.state = {
            email: "",
            password: "",
            errors: {},
        };
    }
    componentWillReceiveProps(nextProps) {
        if (nextProps.UI.errors) {
            this.setState({ errors: nextProps.UI.errors })
        }
    }


    handleSubmit = (event) => {
        event.preventDefault();
        const userData = {
            email: this.state.email,
            password: this.state.password,
        };

        this.props.loginUser(userData, this.props.history);
    };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value,
        });
    };

    render() {
        const {
            UI: { loading },
        } = this.props;
        const { errors } = this.state;
        console.log(loading)
        return (
            <MuiThemeProvider theme={theme}>
                <Grid container columns="equal"  >
                    <Grid.Column />
                    <Grid.Column style={{ marginTop: 20 , background: "#004d40" }}>
                        <Grid columns="equal"  style={{ marginTop: 50 }}>
                            <Grid.Row style={{ padding: '1.2em', margin: 0 }} className="animated rotateInDownRight delay-1s">
                                <Header inverted floated="left" as="h2" className="animated pulse delay-3s">
                                     <span> <Icon name="meetup" size="massive" /></span>
                                    <Header.Content style={{ fontSize: 20 }}>Meet-Friends </Header.Content>
                                    <Header.Content style={{ fontSize: 10, color: "#000" }} className="animated slideInLeft">{errors.message && (<Typography
                                        // color="primary"
                                        variant="body2">{errors.message}</Typography>)} </Header.Content>
                                </Header>
                            </Grid.Row>
                             <Grid.Row>


                                    <form noValidate  onSubmit={this.handleSubmit} >

                                        <TextField id="email" name="email" type="email" label="Email"
                                            value={this.state.email}
                                            onChange={this.handleChange}
                                            helperText={errors.email}
                                            variant="outlined"
                                            error={errors.email ? true : false}
                                            fullWidth
                                            color="primary"

                                            InputProps={{
                                                style: {
                                                    color: "#fff"
                                                }
                                            }}
                                        /> <br />
                                        <TextField id="password" name="password" type="password" label="Password"
                                            value={this.state.password}
                                            onChange={this.handleChange}
                                            helperText={errors.password}
                                            error={errors.password ? true : false}
                                            color="primary"
                                        /> <br /> <br />
                                        {errors.general && (<Typography variant="body2">{errors.general}</Typography>)}
                                        <Button type="submit" variant="contained" id="button" color="primary" disabled={loading}>Login
{loading && (<CircularProgress size={30} disableShrink />)}
                                        </Button><br />
                                        <small className=""> Don't have an account ? Signup{" "} </small>
                                    </form>


                                <br />
                            </Grid.Row>

                        </Grid>
                       <span> <Icon name="meetup" size="huge" /></span>
                        <Google />
                        <Github />
                    </Grid.Column>
                    <Grid.Column >
                    </Grid.Column>
                </Grid>

            </MuiThemeProvider>
        )
    }
}

login.propTypes = {
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired,
    loginUser: PropTypes.func.isRequired,


}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})


export default connect(mapStateToProps, { loginUser })(login)
