import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import { BrowserRouter as Router, Route, Switch, withRouter } from "react-router-dom";
import login from './pages/login/login'
import {Provider,connect} from "react-redux"
import AuthRoute from  "./Auth/AuthRoute"
import store from "./redux/store/store"
import firebase from "./Auth/firebase"
import * as serviceWorker from './serviceWorker';
import { SET_AUTHENTICATED, SET_UNAUTHENTICATED } from './redux/store/types';
import axios from "axios"
import jwt_decode from "jwt-decode"
import {setAuthenticatedUser} from './redux/actions/userActions'

class Root extends Component {
  state = {
    latitude: "",
    longitude: ""
  }



  componentDidMount(){



    firebase.auth().onAuthStateChanged(user=> {
      if(user){
        this.props.setAuthenticatedUser(user)
        const {xa} = user
        store.dispatch({
        type: SET_AUTHENTICATED,
        payload: user
        })
        this.props.history.push("/")
        const token = `Bearer ${xa}`
       if(jwt_decode(xa)['exp'] * 1000 < Date.now()){ 
         store.dispatch({type: SET_UNAUTHENTICATED})
         this.props.history.push("/login")
       }
        axios.defaults.headers.common["Authorization"] = token
      }
      else{
      store.dispatch({type: SET_UNAUTHENTICATED})
      this.props.history.push( '/login')

      }

    })
  } 


  componentDidUpdate(prevProps, prevState) {
    // If prevState changes
    if (prevState.newLatitude !== this.state.newLatitude) {
        // calculate the distance between initial and new coordinates
        this.getDistance();
    }
}


  componentWillUnmount() {
    // Remember to clear all listeners
    navigator.geolocation.clearWatch(this.watcher);
}

  getLocation() {
    navigator.geolocation.getCurrentPosition((position) => {
        this.setState({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
        }, () => {
           console.log(this.state.latitude)
        });
    },
    (error) => {
        //Handle error
    },
    { enableHighAccuracy: false, timeout: 200000, maximumAge: 1000 },
    );
}

watchUserPosition() {
    this.watcher = navigator.geolocation.watchPosition(
      (position) => {
        this.setState({
          newLatitude: position.coords.latitude,
          newLongitude: position.coords.longitude,
          error: null,
        });
      },
      (error) => this.setState({ error: error.message }),
      { enableHighAccuracy: true, timeout: 20000, maximumAge: 1000, distanceFilter: 10 },
    );
  }

  render(){
    return(


         <Switch>
           <AuthRoute exact path="/login" component={login} />
           <Route exact path="/" component={App} />
         </Switch>


    )
  }
}

const RootWithAuth = withRouter(connect(null, {setAuthenticatedUser})(Root))


ReactDOM.render(
  <Provider store={store}>
 <Router>
   <RootWithAuth/>
 </Router>
 </Provider>,

  document.getElementById('root')
);
serviceWorker.register()
