import React, { Component, Fragment } from 'react'
import { Grid, Header, Icon, Dropdown } from "semantic-ui-react"
import firebase from "../../Auth/firebase"
import store from "../../redux/store/store"
import {SET_UNAUTHENTICATED} from "../../redux/store/types"
import Button from "@material-ui/core/Button"
import CircularProgress from "@material-ui/core/CircularProgress";



class userpanel extends Component {
    state = {
        user: {}
    }

    dropDownOptions = () => [
        { key:"user",text: <span >Signed as <strong>User</strong></span>,disabled: true},
        {key: "avatar",text: <span>Change Avatar</span> },
        { key: "signout",text: <span><Button onClick={this.handleSignout } type="submit" variant="contained" id="button" style={{ color:"#607d8b",backgroundColor:"#795548"}}>SignOut</Button></span>}
    ]
    handleSignout =()=> {
        firebase.auth().signOut().then(()=> {
            store.dispatch({type: SET_UNAUTHENTICATED
            })

        })
    }

    componentWillReceiveProps(nextProps){
        if(Object.keys(nextProps.user.currentUser).length> 0){
            this.setState({user: this.props.user.currentUser})
        }
    }

    render() {
        const {user} =this.state
        return (
            <Grid  style={{ background: "#004d40" }}>
                <Grid.Column>
                    <Grid.Row style={{ padding: '1.2em', margin: 0 }}>
                        <Header inverted floated="left" as="h2"  className="animated slideInLeft delay-1s">
                            <Icon name="meetup" />
                            <Header.Content style={{ fontSize: 20 }} >Meet-Friends</Header.Content>
                        </Header>
                    </Grid.Row>
                    <Header style={{ padding: '0.25em' }} as="h4" inverted>
                        <Dropdown trigger={
                               <Fragment>
                                {Object.keys(user).length > 0 ? (
                                <span>
                                {user.username}
                                </span> 
                                ):(
                                    <CircularProgress size={30} disableShrink />
                                )}
                               </Fragment>

                        }
                            options={this.dropDownOptions()}
                        />
                    </Header>
                </Grid.Column>
            </Grid>
        )
    }
}



export default userpanel
