import React, { Component, Fragment } from 'react'
import { Menu, Icon,Modal,Form,Input,Button } from "semantic-ui-react"
import firebase from "../../Auth/firebase"
import Spinner from "../spinner/spinner"
import admin from "firebase-admin"
import Config from "../../config/config.json"
admin.initializeApp({
    credential: admin.credential.cert(Config),
    storageBucket: "portfolio-alex-eb3ef.appspot.com",
    databaseURL: "https://portfolio-alex-eb3ef.firebaseio.com"

})
const db = admin.firestore()

class Channels extends Component {
    state = {
        channels: [],
        channelName: "",
        channelDetails: "",
        modal: false,
        channelsRef: firebase.database().ref('channels'),
        errors:{}

    }
    closeModal = () => {this.setState({modal: false})}

    openModal = () => {this.setState({modal: true}) }

    handleChange =(event) => {
        event.preventDefault()
        this.setState({[event.target.name]: event.target.value})
    }

    addChannel = () => {
        const {channelsRef,channelName,channelDetails,user} = this.state
        const {username,email,userId,profileImage} = this.props.authenticatedUser
        const key = channelsRef.push().key
        const newChannel = {
           id: key,
           name:channelName,
           details: channelDetails,
           createdBy: {
            name:username,
            email: email,
            avatar:profileImage,
            userId: userId
           }
       }
       db.collection("channels").where("name", "==", newChannel.name).get().then(channel=> {
           let errors = {}
           if(!channel.empty){
               errors.channelName = "Already in use"
               this.setState({errors: errors})
           }


       })
    //    channelsRef.orderByKey().on("child_added", snapshot=> {
    //        console.log(snapshot.key)
    //    })
       console.log(newChannel)
    }
    handleSubmit =(event) => {
        event.preventDefault()
        if(this.isformValid(this.state)){
        this.addChannel()
        }
    }
    isformValid = ({channelName,channelDetails}) => channelName && channelDetails;



    render() {
        const { modal,channels,errors } = this.state
        return (
            <Fragment>
                <Menu.Menu style={{ paddingBottom: "2em" }}>
                <Menu.Item>
                    <span><Icon name="exchange" /> CHANNELS </span> {" "}
               ({channels.length}) <Icon name="add" onClick={this.openModal} />

                </Menu.Item>

            </Menu.Menu>

            <Modal basic open={modal} onClose={this.closeModal}>
                {this.props.authenticatedUser? (
                  <Fragment>
                        <Modal.Header>Add a Channel </Modal.Header>
                    <Modal.Content>
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Field>
                                <Input fluid label="Name of channel" name="channelName" onChange={this.handleChange}
                                error={errors.channelName ? true: false}

                                />
                            </Form.Field>
                            <Form.Field>
                                 <Input fluid label="About the channel" name="channelDetails" onChange={this.handleChange}/>
                            </Form.Field>
                        </Form>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button inverted color="green" onClick={this.handleSubmit}>
                            <Icon name="checkmark" /> Add
                        </Button>
                        <Button inverted color="red" onClick={this.closeModal}>
                            <Icon name="remove" /> Cancel
                        </Button>
                    </Modal.Actions>
                  </Fragment>
                ):(<Spinner/>)}

            </Modal>
            </Fragment>

        )
    }
}


export default Channels
