import React, { Component } from 'react'
import LandingPage from './components/landingPage/landingPage'
import './App.css';
import { connect } from "react-redux"
import PropTypes from "prop-types"
import Login from "./pages/login/login"
import { setAuthenticatedUser } from "./redux/actions/userActions"
import Spinner from "./components/spinner/spinner"

class App extends Component {

  state = {
    authenticated: false,
    loading: true

  }
  componentWillReceiveProps(nextProps) {
    if(nextProps.user.authenticatedUser !== null){
      console.log(nextProps.user.authenticatedUser)
      this.setState({authenticated:  this.props.user.authenticated, loading: false})
    }
  }

  render() {
    const { authenticated } = this.state
    console.log(authenticated)
    return (
      <div>
        {authenticated ? (<LandingPage />) : (<Spinner />)}

      </div>

    )
  }
}
App.propTypes = {
  user: PropTypes.object.isRequired,
  setAuthenticatedUser: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  user: state.user
})


export default connect(mapStateToProps, { setAuthenticatedUser })(App)
