import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField'
import Paper from '@material-ui/core/Paper'
import Card from '@material-ui/core/Card'
import Google from "../../components/signInMethods/googleAuth"
import PropTypes from "prop-types"
import { connect } from "react-redux"


class login extends Component {

    constructor(){
        super()
        this.state={
            errors: {}
        }
    }
    componentWillReceiveProps(nxtProps){
        if(nxtProps.UI.errors){
            this.setState({errors: nxtProps.UI.errors})
        }
    }


    render() {
        return (
            <div className="table-responsive">

                    <div className="sidebar">
                    <Paper>
                        <Card>
                            <Google/>
                            <TextField type="email" label="Email" variant="outlined" fullWidth />
                            <TextField type="password" label="Password" variant="outlined" fullWidth />
                        </Card>
                    </Paper>
                    </div>

            </div>
        )
    }
}

login.propTypes = {
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired,

}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

export default connect(mapStateToProps)(login)
