import React, { Component, Fragment } from 'react'
import { Menu, Icon,Modal,Form,Input,Button } from "semantic-ui-react"
import {addChannel} from "../../redux/actions/dataActions"
import {connect} from "react-redux"
import firebase from "../../Auth/firebase"

class Channels extends Component {
    state = {
        channels: [],
        channelName: "",
        channelDetails: "",
        modal: false,
        user: this.props.user.authenticatedUser,
        channelsRef: firebase.database().ref('channels'),

    }
    closeModal = () => {this.setState({modal: false})}

    openModal = () => {this.setState({modal: true}) }

    handleChange =(event) => {
        event.preventDefault()
        this.setState({[event.target.name]: event.target.value})
    }

    addChannel = () => {
        const {channelsRef,channelName,channelDetails,user} = this.state
    //     const key = channelsRef.push().key
    //     const{username,email,userId} = user
    //     const newChannel = {
    //        id: key,
    //        name:channelName,
    //        details: channelDetails,
    //        createdBy: {
    //         name:username,
    //         email: email,
    //         userId: userId
    //     }

    //    }
       console.log(user)
    }
    handleSubmit =(event) => {
        event.preventDefault()
        if(this.isformValid(this.state)){
        this.addChannel()
        }
    }
    isformValid = ({channelName,channelDetails}) => channelName && channelDetails;



    render() {
        const { modal,channels } = this.state

        return (
            <Fragment>
                <Menu.Menu style={{ paddingBottom: "2em" }}>
                <Menu.Item>
                    <span><Icon name="exchange" /> CHANNELS </span> {" "}
               ({channels.length}) <Icon name="add" onClick={this.openModal} />

                </Menu.Item>

            </Menu.Menu>

            <Modal basic open={modal} onClose={this.closeModal}>
                <Modal.Header>Add a Channel </Modal.Header>
                    <Modal.Content>
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Field>
                                <Input fluid label="Name of channel" name="channelName" onChange={this.handleChange}/>
                            </Form.Field>
                            <Form.Field>
                                 <Input fluid label="About the channel" name="channelDetails" onChange={this.handleChange}/>
                            </Form.Field>
                        </Form>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button inverted color="green" onClick={this.handleSubmit}>
                            <Icon name="checkmark" /> Add
                        </Button>
                        <Button inverted color="red" onClick={this.closeModal}>
                            <Icon name="remove" /> Cancel
                        </Button>
                    </Modal.Actions>

            </Modal>
            </Fragment>

        )
    }
}

const mapStateToProps = (state) => ({
    user: state.user
})


export default connect(mapStateToProps) (Channels)
