import React, { Component, Fragment } from 'react'
import { Segment, Comment } from "semantic-ui-react"
import MessagesHeader from "./messageHeader"
import MessageForm from "./messageForm"
import firebase from "../../Auth/firebase"
import Message from "./Message"
import Spinner from "../spinner/spinner"

class Messages extends Component {

    state = {
        messagesRef: firebase.database().ref('messages'),
        channel: this.props.currentChannel,
        currentUser: this.props.authenticatedUser,
        messages: [],
        loading: true,
        numUniqueUsers: "",
        searchTerm: "",
        searchLoading: false,
        searchResults:[]
    }

    componentDidMount() {
        const { channel, currentUser } = this.state
        if (channel && currentUser) {
            this.addListeners(channel.id)
        }
    }

    addListeners = (channelId) => {
        this.addMessageListener(channelId)
    }

    addMessageListener = (channelId) => {
        let loadedMessages = []
        this.state.messagesRef.child(channelId).on("child_added", snap => {
            loadedMessages.push(snap.val())
            this.setState({ messages: loadedMessages, loading: false })
        })
        this.countUniqueUsers(loadedMessages)
    }

    countUniqueUsers =(messages)=> {
        const uniqueUsers = messages.reduce((acc, message)=> {
            console.log(message.user)
            if(!acc.includes(message.user.id)){
                acc.push(message.user.id)
            }
            return acc
        }, [])

        const plural = uniqueUsers.length > 1 || uniqueUsers.length === 0;
    const numUniqueUsers = `${uniqueUsers.length} user${plural ? "s" : ""}`;
    this.setState({ numUniqueUsers });
    }

    displayMessages = (messages) => (
        messages.length > 0 ? messages.map(message => (
            <Message
                key={message.timestamp}
                message={message}
                user={this.state.currentUser}
            />
        )):<Spinner
            messages={messages}
        />
    )

    displayChannelName = (channel) => channel ? `${channel.name}`: ""

    handleSearch = (event) => {
        this.setState({
            searchTerm: event.target.value,
            searchLoading: true
        },
        ()=>{
            this.handleSearchMessages()
        }
        )
    }
    handleSearchMessages =()=> {
        const channelMessages =[...this.state.messages] 
        const regex = new RegExp(this.state.searchTerm, 'gi')
        const searchResults = channelMessages.reduce((acc, message)=>{
             if(message.content && message.content.match(regex) || message.user.name.match(regex)){
                 acc.push(message)
             }
             return acc
        },[]);
        this.setState({ searchResults:searchResults})
        setTimeout(()=> this.setState({ searchLoading: false}),1000)
    }

    render() {
        const { messagesRef, channel, currentUser, messages,numUniqueUsers,searchTerm,searchResults,searchLoading } = this.state
        return (
            <Fragment>
               <MessagesHeader
               handleSearch={this.handleSearch} 
               channelName={this.displayChannelName(channel)}
               numUniqueUsers={numUniqueUsers}
               searchLoading={searchLoading}
               />
                        <Segment>
                            <Comment.Group className="messages">
                                {searchTerm ? this.displayMessages(searchResults): this.displayMessages(messages)}

                            </Comment.Group>
                        </Segment>
                        <MessageForm
                            messagesRef={messagesRef}
                            currentChannel={channel}
                            currentUser={currentUser}
                        />
            </Fragment>
        )
    }
}

export default Messages
