import React, { Component } from 'react'
import { authenticateGoogle } from "../../redux/actions/userActions"
import PropTypes from "prop-types"
import { connect } from "react-redux"
import Button from "@material-ui/core/Button";
import GoogleLogin from 'react-google-login';



class googleAuth extends Component {
    componentWillReceiveProps(nxtProps) {
        if (nxtProps.UI.errors) {
            this.setState({ errors: nxtProps.UI.errors })
        }
    }

    handleLogin = (event) => {
        event.preventDefault()
        this.props.authenticateGoogle()
    }

    render() {
        return (
            <Button
            variant="contained"
                // color="secondary"
                onClick={this.handleLogin}>
                {/* <span>google</span> */}
                <GoogleLogin/>
            </Button>
        )
    }
}

googleAuth.propTypes = {
    authenticateGoogle: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired

}

const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

const mapActionsToProps = {
    authenticateGoogle
}

export default connect(mapStateToProps, mapActionsToProps)(googleAuth)
