import { SET_CURRENT_CHANNEL} from "../store/types"

const initialChannelState = {
    // currentChannel: null,
}

export const currentChannelReducer =(state = initialChannelState, action) =>{
    switch(action.types){
        case SET_CURRENT_CHANNEL:return{...state, ...action.payload}
        default: return state
    }
}
