import {SET_GOOGLE_USER,SET_AUTHENTICATED,SET_UNAUTHENTICATED,LOADING_USER,SET_USER} from "../store/types"
const initialState = {
    authenticated: false,
    loading: false,
    currentUser: null
}

export default function(state = initialState,action) {
    switch(action.type){
        case SET_AUTHENTICATED:return{...state,authenticated:true, currentUser: action.payload}
        case SET_UNAUTHENTICATED:return initialState
        case SET_GOOGLE_USER:return{authenticated:true, loading: false,...action.payload}
        case LOADING_USER:return{...state,loading:true}
        default: return state

    }

}
