import React, { Component } from 'react'
import { Segment, Button, Input } from "semantic-ui-react"
import firebase from "../../Auth/firebase"
import FileModal from "./fileModal"
import uuidv4 from "uuid/v4"

class messageForm extends Component {
    state = {
        storageRef: firebase.storage().ref(),
        uploadState: "",
        uploadTask: null,
        message: "",
        loading: false,
        channel: this.props.currentChannel,
        user: this.props.currentUser,
        messagesRef: this.props.messagesRef,
        errors: [],
        modal: false,
        percentUploaded:0
    }
    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }

    createMessage = () => {
        const { user } = this.state
        const message = {
            timestamp: firebase.database.ServerValue.TIMESTAMP,
            user: {
                id: user.userId,
                name: user.username,
                avatar: user.profileImage
            },
            content: this.state.message
        }
        return message
    }

    openModal = () => this.setState({ modal: true })
    closeModal = () => this.setState({ modal: false })



    uploadFile = (file, metadata) => {
        const pathToUpload = this.state.channel.id
        const ref = this.props.messagesRef
        const filePath = `chat/public/${uuidv4()}.jpg` 

        this.setState({
            uploadState: "uploading",
            uploadTask: this.state.storageRef.child(filePath).put(file, metadata)
        },
        ()=> {
            this.state.uploadTask.on('state_changed', snap=> {
                const percentUploaded = Math.round((snap.bytesTransferred/snap.totalBytes) *100)
                this.setState({percentUploaded: percentUploaded})
            })
        }
        )

    };



    sendMessage = () => {
        const { message, channel, messagesRef } = this.state
        if (message) {
            this.setState({ loading: true })
            messagesRef.child(channel.id).push().set(this.createMessage())
                .then(() => {
                    this.setState({ loading: false, message: "", errors: [] })
                }).catch((error) => {
                    console.error(error)
                    this.setState({
                        loading: false,
                        errors: this.state.errors.concat(error)
                    })
                })

        } else {
            this.setState({
                errors: this.state.errors.concat({ message: 'Add a message' })
            })
        }
    }

    render() {
        const { errors, message, loading, modal } = this.state
        return (
            <Segment className="message__form">
                <Input fluid name="message" style={{ marginBottom: '0.7em' }}
                    label={<Button icon={'add'} />}
                    value={message}
                    labelPosition="left"
                    placeholder="Write your message"
                    onChange={this.handleChange}
                    className={
                        errors.some(error => error.message.includes('message')) ? 'error' : ''
                    }

                />
                <Button.Group icon widths="2">
                    <Button
                        color="orange"
                        content="Add Reply"
                        labelPosition="left"
                        icon="edit"
                        onClick={this.sendMessage}
                        disabled={loading}
                    />
                    <FileModal
                        modal={modal}
                        closeModal={this.closeModal}
                        uploadFile={this.uploadFile}

                    />
                    <Button
                        color="teal"
                        content="Upload Media"
                        labelPosition="right"
                        icon="cloud upload"
                        onClick={this.openModal}

                    />
                </Button.Group>
            </Segment>
        )
    }
}

export default messageForm
