        }
    }

    handleLogin() {
        this.props.authenticateGoogle(this.props.history)
    }


    render() {
        return (
            <button onClick={() => this.handleLogin()}>
                Google
            </button>
        )
    }
}

// export default googleAuth

googleAuth.propTypes = {
    authenticateGoogle: PropTypes.func.isRequired
}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

export default connect(mapStateToProps, { authenticateGoogle })(googleAuth)
