import {SET_GOOGLE_USER,SET_AUTHENTICATED,SET_UNAUTHENTICATED,SET_USER} from "../store/types"
const initialState = {
    authenticated: false,
    currentUser: null
}

export const userReducer = (state = initialState,action) => {
    switch(action.type){
        case SET_AUTHENTICATED:return{authenticated:true}
        case SET_USER: return{ currentUser: action.payload}
        case SET_UNAUTHENTICATED:return {...initialState}
        case SET_GOOGLE_USER:return{authenticated:true,...action.payload}
        default: return state

    }

}
