import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import { BrowserRouter as Router, Route, Switch, withRouter } from "react-router-dom";
import login from './pages/login/login'
import {Provider,connect} from "react-redux"
import AuthRoute from  "./Auth/AuthRoute"
import store from "./redux/store/store"
import firebase from "./Auth/firebase"
import * as serviceWorker from './serviceWorker';
import { SET_AUTHENTICATED, SET_UNAUTHENTICATED, STOP_LOADING_UI } from './redux/store/types';
import axios from "axios"
import jwt_decode from "jwt-decode"
import {setAuthenticatedUser,getCurrentUser} from './redux/actions/userActions'

class Root extends Component {

  setToken(token,email){
    if(token){
      axios.defaults.headers.common["Authorization"] = token
      this.props.getCurrentUser(email)
    }
  }

  componentDidMount(){
    firebase.auth().onAuthStateChanged(user=> {
      if(user){
        const {xa,uid,email} = user
        const token = `Bearer ${xa}`
        this.setToken(token,email)
        this.props.setAuthenticatedUser(user)

        store.dispatch({type:STOP_LOADING_UI})
        this.props.history.push("/")



       if(jwt_decode(xa)['exp'] * 1000 < Date.now()){ 
         store.dispatch({type: SET_UNAUTHENTICATED})
         this.props.history.push("/login")
       }
      }
      else{
      this.props.history.push( '/login')

      }

    })

  }  

  render(){
    console.log(this.props)
    return(


         <Switch>
           <Route exact path="/login" component={login} />
           <Route exact path="/" component={App} />
         </Switch>


    )
  }
}

const RootWithAuth = withRouter(connect(null, {setAuthenticatedUser,getCurrentUser})(Root))


ReactDOM.render(
  <Provider store={store}>
 <Router>
   <RootWithAuth/>
 </Router>
 </Provider>,

  document.getElementById('root')
);
serviceWorker.register()
