import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import { BrowserRouter as Router, Route, Switch, withRouter } from "react-router-dom";
import login from './pages/login/login'
import {Provider,connect} from "react-redux"
import AuthRoute from  "./Auth/AuthRoute"
import store from "./redux/store/store"
import firebase from "./Auth/firebase"
import * as serviceWorker from './serviceWorker';
import { SET_AUTHENTICATED, SET_UNAUTHENTICATED } from './redux/store/types';
import axios from "axios"
import jwt_decode from "jwt-decode"
import {setAuthenticatedUser} from './redux/actions/userActions'
import Spinner from "./components/spinner/spinner"
import PropTypes from "prop-types"

class Root extends Component {

  componentDidMount(){
    firebase.auth().onAuthStateChanged(user=> {
      if(user){
        this.props.setAuthenticatedUser(user)
        const {xa} = user
        console.log(user)
        store.dispatch({type: SET_AUTHENTICATED,
        payload: user
        })

        this.props.history.push("/")
        const token = `Bearer ${xa}`
       if(jwt_decode(xa)['exp'] * 1000 < Date.now()){ 
         store.dispatch({type: SET_UNAUTHENTICATED})
         this.props.history.push("/login")
       }
        axios.defaults.headers.common["Authorization"] = token
      }
      else{
      // store.dispatch({type: SET_UNAUTHENTICATED})
      this.props.history.push( '/login')

      }

    })
  }  

  render(){
    const {authenticated} = this.props
    console.log(authenticated)
    return(

      <Router>
         <Switch>
           <AuthRoute exact path="/login" component={login} />
           <AuthRoute exact path="/" component={App} />
         </Switch>
       </Router>

    )
  }
}
Root.propTypes = {
  user: PropTypes.object.isRequired
}

const mapStateToProps = (state) => {
  user: state.user
}

const RootWithAuth = withRouter(connect(mapStateToProps, {setAuthenticatedUser})(Root))


ReactDOM.render(
  <Provider store={store}>
 <Router>
   <RootWithAuth/>
 </Router>
 </Provider>,

  document.getElementById('root')
);
serviceWorker.register()
