import React, { Component } from "react";
import { Header, Segment, Input, Icon,Image } from "semantic-ui-react";
import { CircularProgress } from "@material-ui/core";

class messageHeader extends Component {
  render() {
    const {
      channelName,
      numUniqueUsers,
      handleSearch,
      searchLoading,
      privateChannel,
      channel,
    } = this.props;

    return (
      <Segment clearing>
        <Header fluid="true" as="h2" floated="left" style={{ marginBottom: 0 }}>
          <span>
            {channel && channel.photoURL ?  <Image src={channel.photoURL} spaced="right" avatar />:""}
            {channelName}
           {!privateChannel &&  <Icon name={"star outline"} color="black" /> }
          </span>
          <Header.Subheader>
            {numUniqueUsers ? (
              numUniqueUsers
            ) : (
              <CircularProgress size={10} disableShrink />
            )}
          </Header.Subheader>
        </Header>
        <Header floated="right">
          <Input
            loading={searchLoading}
            onChange={handleSearch}
            size="mini"
            icon="search"
            name="searchTerm"
            placeholder="Search Messages"
          />
        </Header>
      </Segment>
    );
  }
}

export default messageHeader;
