import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField'

import Google from "../../components/signInMethods/googleAuth"
import Github from "../../components/signInMethods/githubAuth"
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

import PropTypes from "prop-types"
import { connect } from "react-redux"


class login extends Component {

    constructor(){
        super()
        this.state={
            errors: {}
        }
    }
    componentWillReceiveProps(nxtProps){
        if(nxtProps.UI.errors){
            this.setState({errors: nxtProps.UI.errors})
        }
    }


    render() {
        return (


            <div className="main-panel" id="main-panel">
            <div className="panel-header panel-header-sm"></div>
            <div className="content">
              <div className="rows">
                <div className="col-md-6" id="col">
                  <div className="card animated slideInDown delay-1s">
                    <div className="card-header">
                      <h4 className="card-title"> Login</h4>
                    </div>
                    <div className="card-body">
                      <div className="content">
                        <Grid container className="container">
                          <Grid item sm={6} className=" form " id="cf">

                            <form noValidate className="">
                              <TextField
                                id="email"
                                name="email"
                                type="email"
                                label="Email"

                                // variant="outlined"
                              />
                              <TextField
                                id="password"
                                name="password"
                                type="password"
                                label="Password"


                                // variant="outlined"
                              />


                              <Button
                                // variant="outlined"
                                type="submit"
                                variant="contained"
                                id="button"
                                color="secondary"


                              >
                               Login
                              </Button>
                              <br />
                              <hr />
                              <small className="">
                                Don't have an account ? Signup{" "}
                                {/* <Link to="/signup" className="link">
                                  here
                                </Link> */}
                              </small>
                              <br />

                            </form>
                          </Grid>
                        </Grid>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
           )
    }
}

login.propTypes = {
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired,

}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

export default connect(mapStateToProps)(login)
