import React, { Component } from 'react'
import TextField from '@material-ui/core/TextField'
import Paper from '@material-ui/core/Paper'
import Card from '@material-ui/core/Card'
import Google from "../../components/signInMethods/googleAuth"
import Github from "../../components/signInMethods/githubAuth"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import { loginUser } from "../../redux/actions/userActions"
import Typography from "@material-ui/core/Typography";
import PropTypes from "prop-types"
import { connect } from "react-redux"
import CircularProgress from "@material-ui/core/CircularProgress";


class login extends Component {

    constructor() {
        super()
        this.state = {
            email: "",
            password: "",
            errors: {}
        }
    }
    componentWillReceiveProps(nxtProps) {
        if (nxtProps.UI.errors) {
            this.setState({ errors: nxtProps.UI.errors })
        }
    }
    handleSubmit = (event) => {
        event.preventDefault();
        this.setState({
            loading: true,
        });
        const userData = {
            email: this.state.email,
            password: this.state.password,
        };

        this.props.loginUser(userData, this.props.history);
    };
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value,
        });
    };


    render() {

        const {
            UI: { loading },
        } = this.props;
        const { errors } = this.state;


        return (
            <Grid container direction="row">
            <Grid item xs={12} md={3} />   
            <Grid item xs={12} md={6}>
               <Grid container direction="column">
                   <Grid item xs={12} sm={6}/>
                   <Grid item xs={12} sm={3}>
                   <form noValidate onSubmit={this.handleSubmit} className="table-responsive" >
                    <TextField id="email" name="email" type="email" label="Email"
                        value={this.state.email}
                        onChange={this.handleChange}
                        helperText={errors.email}
                        error={errors.email ? true : false}
                    /> <br />
                    <TextField id="password" name="password" type="password" label="Password"
                        value={this.state.password}
                        onChange={this.handleChange}
                        helperText={errors.password}
                        error={errors.password ? true : false}

                    /> <br /> <br />
                    {errors.general && (<Typography variant="body2">{errors.general}</Typography>)}
                    <Button type="submit" variant="contained" id="button" color="secondary" disabled={loading}>Login
    {loading && (<CircularProgress size={30} disableShrink />)}
                    </Button>
                    <small className=""> Don't have an account ? Signup{" "} </small>
                </form>
                   </Grid>
                   <Grid item xs={12} sm={3}>
                        <Google/>
                   </Grid>

               </Grid>
            </Grid>
            <Grid item xs={12} md={3} /> 
        </Grid>
        )
    }
}

login.propTypes = {
    user: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired,
    loginUser: PropTypes.func.isRequired,

}


const mapStateToProps = (state) => ({
    user: state.user,
    UI: state.UI
})

export default connect(mapStateToProps, { loginUser })(login)
