                }
           axios.post(`/google/${this.state.userId}`,userData).then(res=> {
               console.log(res.data)
           }).catch(err=> {
              console.log(err.message)
          })
            }     
          })

          .catch(err=> {
              console.log(err.message)
          })
    }


    render() {
        return (
            <button onClick={()=> this.handleLogin()}>
               Google 
            </button>
        )
    }
}

export default googleAuth
