import {SET_GOOGLE_USER,SET_AUTHENTICATED,SET_UNAUTHENTICATED,LOADING_USER,SET_USER} from "../store/types"
const initialState = {
    authenticated: false,
    loading: false,
    authenticatedUser: null
}

export const userReducer = (state = initialState,action) => {
    switch(action.type){
        case SET_AUTHENTICATED:return{authenticatedUser: action.payload,authenticated:true}
        // case SET_USER: return{ currentUser: action.payload.currentUser, loading: false}
        case SET_UNAUTHENTICATED:return {...initialState, loading: false}
        case SET_GOOGLE_USER:return{authenticated:true, loading: false,...action.payload}
        case LOADING_USER:return{...state,loading:true}
        default: return state

    }

}
