import { NEW_CHANNEL,LOADING_DATA} from "../store/types"

const newChannelData = {
    // channels: [],
    newChannel: {},
    loading: false
}

export const add_channel_reducer =(state= newChannelData,action) =>{
    switch(action.types){
        case LOADING_DATA: return{loading: true}
        case NEW_CHANNEL: return{loading: false, newChannel: action.payload}
        default: return state
    }
}
