import {LOADING_DATA,NEW_CHANNEL,SET_ERRORS} from "../store/types"
import axios from "axios"

export const addChannel = (channelData) => (dispatch) => {
dispatch({type: LOADING_DATA})
console.log(channelData)    
axios.post("/add/channel",channelData).then(res=> {
    dispatch({
        type: NEW_CHANNEL,
        payload: res.data
    })
})
.catch((err) => {
    console.log(err)
    dispatch({
      type: SET_ERRORS,
      payload: err
    })
  })

}
