import React, { Component, Fragment } from 'react'
import { Segment, Comment } from "semantic-ui-react"
import MessagesHeader from "./messageHeader"
import MessageForm from "./messageForm"
import firebase from "../../Auth/firebase"
import Message from "./Message"
import Spinner from "../spinner/spinner"

class Messages extends Component {

    state = {
        messagesRef: firebase.database().ref('messages'),
        channel: this.props.currentChannel,
        currentUser: this.props.currentUser,
        messages: [],
        loading: true
    }

    componentDidMount() {
        const { channel, currentUser } = this.state
        if (channel && currentUser) {
            this.addListeners(channel.id)
        }
    }

    addListeners = (channelId) => {
        this.addMessageListener(channelId)
    }

    addMessageListener = (channelId) => {
        let loadedMessages = []
        this.state.messagesRef.child(channelId).on("child_added", snap => {
            loadedMessages.push(snap.val())
            this.setState({ messages: loadedMessages, loading: false })
        })
        this.countUniqueUsers(loadedMessages)
    }

    countUniqueUsers =(messages)=> {
        const uniqueUsers = messages.reduce((acc, message)=> {
            if(!acc.includes(message.user.name)){
                acc.push(message.user.username)
            }
            return acc
        }, [])

        const numUniqueUsers = `${uniqueUsers.length}`
    }

    displayMessages = (messages) => (
        messages.length > 0 ? messages.map(message => (
            <Message
                key={message.timestamp}
                message={message}
                user={this.state.currentUser}
            />
        )):<Spinner
            messages={messages}
        />
    )

    displayChannelName = (channel) => channel ? `${channel.name}`: ""

    render() {
        const { messagesRef, channel, currentUser, messages } = this.state
        return (
            <Fragment>
               <MessagesHeader 
               channelName={this.displayChannelName(channel)}
               />
                        <Segment>
                            <Comment.Group className="messages">
                                {this.displayMessages(messages)}

                            </Comment.Group>
                        </Segment>
                        <MessageForm
                            messagesRef={messagesRef}
                            currentChannel={channel}
                            currentUser={currentUser}
                        />
            </Fragment>
        )
    }
}

export default Messages
